var currentSort = 'author';
var currentDir = 1;
var currentFilter = 'all';

var PHYSICAL_TYPES = ['Paper Book', 'Paperback', 'Hardcover', 'Mass Market Paperback', 'CD audiobook', 'Comic book', 'Electronic', 'Library Binding'];

function isPhysical(b) {
    return b.m !== 'Kindle';
}

function normTitle(t) {
    return (t || '').toLowerCase().replace(/[^a-z0-9\s]/g, '').replace(/\s+/g, ' ').trim();
}

// Precompute titles that exist in both physical and Kindle
var _physTitles = {};
var _kindleTitles = {};
BOOKS.forEach(function(b) {
    var n = normTitle(b.t);
    if (b.m === 'Kindle') _kindleTitles[n] = true;
    else _physTitles[n] = true;
});
var bothTitles = {};
Object.keys(_physTitles).forEach(function(n) {
    if (_kindleTitles[n]) bothTitles[n] = true;
});

function filterByFormat(books, filter) {
    if (filter === 'all') return books;
    if (filter === 'physical') return books.filter(function(b) { return isPhysical(b); });
    if (filter === 'kindle') return books.filter(function(b) { return b.m === 'Kindle'; });
    if (filter === 'both') return books.filter(function(b) { return bothTitles[normTitle(b.t)]; });
    return books;
}

function getLastName(a) {
    if (!a) return '';
    var parts = a.trim().split(' ');
    return parts[parts.length - 1].toLowerCase();
}

function sortBooks(books, field) {
    return books.slice().sort(function(a, b) {
        var va, vb;
        if (field === 'author') {
            va = getLastName(a.a);
            vb = getLastName(b.a);
        } else if (field === 'title') {
            va = (a.t || '').toLowerCase().replace(/^(the |a |an )/, '');
            vb = (b.t || '').toLowerCase().replace(/^(the |a |an )/, '');
        } else if (field === 'year') {
            va = parseInt(a.y) || 0;
            vb = parseInt(b.y) || 0;
            return (vb - va) * currentDir;
        } else if (field === 'pages') {
            va = parseInt(a.p) || 0;
            vb = parseInt(b.p) || 0;
            return (vb - va) * currentDir;
        }
        if (va < vb) return -1 * currentDir;
        if (va > vb) return 1 * currentDir;
        return 0;
    });
}

function getGroupKey(book, field) {
    if (field === 'author') {
        var ln = getLastName(book.a);
        return ln ? ln[0].toUpperCase() : '#';
    } else if (field === 'title') {
        var t = (book.t || '').replace(/^(The |A |An )/i, '').trim();
        var c = t[0] || '#';
        return /[0-9]/.test(c) ? '#' : c.toUpperCase();
    }
    return null;
}

function render(books) {
    var container = document.getElementById('library');
    var frag = document.createDocumentFragment();
    var lastGroup = null;
    var useGroups = (currentSort === 'author' || currentSort === 'title');

    if (books.length === 0) {
        var noRes = document.createElement('div');
        noRes.className = 'no-results';
        noRes.textContent = 'no matches found';
        frag.appendChild(noRes);
        container.innerHTML = '';
        container.appendChild(frag);
        document.getElementById('count').textContent = '0';
        return;
    }

    var list = document.createElement('ul');
    list.className = 'book-list';

    for (var i = 0; i < books.length; i++) {
        var b = books[i];

        if (useGroups) {
            var group = getGroupKey(b, currentSort);
            if (group !== lastGroup) {
                var marker = document.createElement('div');
                marker.className = 'letter-marker';
                marker.textContent = group;
                frag.appendChild(marker);
                if (list.children.length > 0) {
                    frag.appendChild(list);
                    list = document.createElement('ul');
                    list.className = 'book-list';
                }
                lastGroup = group;
            }
        }

        var li = document.createElement('li');
        li.className = 'book-item';

        var info = document.createElement('div');
        var titleSpan = document.createElement('div');
        titleSpan.className = 'book-title' + (b.r ? ' book-reading' : '');
        var titleLink = document.createElement('a');
        var searchQ = b.t + (b.a ? ' ' + b.a : '') + ' book';
        titleLink.href = 'https://duckduckgo.com/?q=' + encodeURIComponent(searchQ);
        titleLink.target = '_blank';
        titleLink.rel = 'noopener';
        titleLink.textContent = b.t;
        titleSpan.appendChild(titleLink);
        info.appendChild(titleSpan);

        if (b.a) {
            var authorSpan = document.createElement('div');
            authorSpan.className = 'book-author';
            var authorLink = document.createElement('a');
            authorLink.href = 'https://duckduckgo.com/?q=' + encodeURIComponent(b.a + ' author');
            authorLink.target = '_blank';
            authorLink.rel = 'noopener';
            authorLink.textContent = b.a;
            authorSpan.appendChild(authorLink);
            info.appendChild(authorSpan);
        }

        if (b.g) {
            var tagsSpan = document.createElement('div');
            tagsSpan.className = 'book-tags';
            var tagList = b.g.split(', ').slice(0, 6).join(' / ');
            tagsSpan.textContent = tagList;
            info.appendChild(tagsSpan);
        }

        var meta = document.createElement('div');
        meta.className = 'book-meta';
        var parts = [];
        if (b.y) parts.push(b.y);
        if (b.p) parts.push(b.p + 'p');
        if (b.m) parts.push(b.m.replace('Paper Book', 'PB').replace('Paperback', 'PB').replace('Hardcover', 'HC').replace('Mass Market Paperback', 'PB').replace('CD audiobook', 'Audio').replace('Comic book', 'Comic').replace('Kindle', 'K'));
        meta.textContent = parts.join(' / ');

        li.appendChild(info);
        li.appendChild(meta);
        list.appendChild(li);
    }

    frag.appendChild(list);
    container.innerHTML = '';
    container.appendChild(frag);
    document.getElementById('count').textContent = books.length.toString();
}

function update() {
    var q = document.getElementById('search').value.toLowerCase().trim();
    var filtered = BOOKS;
    if (q) {
        filtered = BOOKS.filter(function(b) {
            return (b.t && b.t.toLowerCase().indexOf(q) !== -1) ||
                   (b.a && b.a.toLowerCase().indexOf(q) !== -1) ||
                   (b.g && b.g.toLowerCase().indexOf(q) !== -1);
        });
    }
    filtered = filterByFormat(filtered, currentFilter);
    var sorted = sortBooks(filtered, currentSort);
    render(sorted);
}

document.getElementById('search').addEventListener('input', update);

document.querySelectorAll('.sort-btn').forEach(function(btn) {
    btn.addEventListener('click', function() {
        var newSort = this.dataset.sort;
        if (newSort === currentSort) {
            currentDir *= -1;
        } else {
            currentSort = newSort;
            currentDir = 1;
        }
        document.querySelectorAll('.sort-btn').forEach(function(b) { b.classList.remove('active'); });
        this.classList.add('active');
        document.getElementById('sort-label').textContent = currentSort + (currentDir === -1 ? ' (rev)' : '');
        update();
    });
});

document.querySelectorAll('.filter-btn').forEach(function(btn) {
    btn.addEventListener('click', function() {
        currentFilter = this.dataset.filter;
        document.querySelectorAll('.filter-btn').forEach(function(b) { b.classList.remove('active'); });
        this.classList.add('active');
        update();
    });
});

function updateTimestamp() {
    var now = new Date();
    document.getElementById('timestamp').textContent = 'SYS_TIME: ' + now.toISOString().replace('T', ' ').substring(0, 19);
}
updateTimestamp();
setInterval(updateTimestamp, 1000);

update();
